import pandas as pdimport matplotlib.pyplot as pltwd=getcwd() # set working directorychdir(wd)# read in dfyield_df = pd.read_csv(wd + '/data/analysis_results/yield_df_1381151.csv')irrig_df = pd.read_csv(wd + '/data/analysis_results/irrig_df_1381151.csv')#boxplot showing wimas irrigation and irrigation when irrig is trigered different soil moisture threshlosboxplot = irrig_df.boxplot(column=['irrig_wimas', '0', '20', '40', '60', '80', '100'], showmeans=True)boxplot.set_ylabel('Irrigation (mm)')boxplot.set_xlabel('Soil-moisture threshold (%TAW)')#boxplot with USDS NASS yield and Aquacrop yieldboxplot = yield_df.boxplot(column=['YieldUSDA', '0', '20', '40', '60', '80', '100'], showmeans=True)boxplot.set_ylabel('Yield (t/ha)')boxplot.set_xlabel('Soil-moisture threshold (%TAW)')